import java.util.*;

public class XuLy {

	public XuLy() {
		// TODO Auto-generated constructor stub
	}

	public static int tinhTong(int a, int b) {
		int tong = a + b;
		return tong;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 9 , y = 10;
		int ketQua = tinhTong(x, y);
		System.out.println("Tổng là: " + ketQua);
	}

}
